(()=>{"use strict";(()=>{const e=document.querySelector("#form"),t=document.querySelector("#location-input");e.addEventListener("submit",(n=>{n.preventDefault(),(async e=>{try{let t=await fetch(`https://api.weatherapi.com/v1/current.json?key=f6b065bd87b3492992e183554230606&q=${e}`);return await t.json()}catch(e){console.error(e)}})(t.value).then((e=>(e=>{const t=document.querySelector(".weather-content"),n=e.location.name,i=e.location.country,o=e.location.localtime.split(" ")[0],r=e.location.localtime.split(" ")[1],c=e.current.condition.text,a=e.current.feelslike_c,l=e.current.feelslike_f;t.innerHTML=`\n  <p id="locationName">City: ${n}</p>\n  <p id="countryName">Country: ${i}</p>\n  <p id="localDate">Date: ${o}</p>\n  <p id="localTime">Time: ${r}</p>\n  <p id="currentCondition">Current Condition: ${c}</p>\n  <p id="tempInCelsius">Temperature in C: ${a}</p>\n  <p id="tempInFahreneit">Temperature in F: ${l}</p>\n  `})(e))),e.reset()}))})()})();